import React, { useState } from 'react';
import "./info.scss";
import CloseIcon from '@mui/icons-material/Close';
const Info = ({onClose}) => {
    const tutorialData = [
        {
            text: "Welcome to the Home screen! At the top you can see some functionality, starting from the left the home icon takes you to the home page, the Moon/soon triggers ",
            imageSrc: "/images/page1.jpg"
        },
        {
            text: "This is the second page.",
            imageSrc: "/images/page2.jpg"
        },
        {
            text: "Yet another page of information.",
            imageSrc: "/images/page3.jpg"
        },
        {
            text: "And this is the final page!",
            imageSrc: "/images/page4.jpg"
        }
    ];

    const [currentPage, setCurrentPage] = useState(0);

    const handleNext = () => {
        if (currentPage < tutorialData.length - 1) {
            setCurrentPage(currentPage + 1);
        }
    };

    const handlePrevious = () => {
        if (currentPage > 0) {
            setCurrentPage(currentPage - 1);
        }
    };

    const handleExit = () => {
        if (onClose) {
            onClose();
        }
    };


    return (
        <div className='info'>
            <CloseIcon className="exit-button" onClick={handleExit}/>
            <img src={tutorialData[currentPage].imageSrc} alt="Tutorial Step" />
            <p>{tutorialData[currentPage].text}</p>
            <button onClick={handlePrevious} disabled={currentPage === 0}>
                Previous
            </button>
            <button onClick={handleNext} disabled={currentPage === tutorialData.length - 1}>
                Next
            </button>
        </div>
    )
}

export default Info;
