import React, { useEffect, useRef } from 'react';
import "./login.scss"

const Login = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Setting up the letters
    var letters = 'ABCDEFGHIJKLMNOPQRSTUVXYZ'.repeat(6);
    letters = letters.split('');

    var fontSize = 10;
    var columns = canvas.width / fontSize;

    var drops = [];
    for (var i = 0; i < columns; i++) {
      drops[i] = 1;
    }

    function draw() {
      ctx.fillStyle = 'rgba(0, 0, 0, .1)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      for (var i = 0; i < drops.length; i++) {
        var text = letters[Math.floor(Math.random() * letters.length)];
        ctx.fillStyle = '#0f0';
        ctx.fillText(text, i * fontSize, drops[i] * fontSize);

        drops[i]++;
        if (drops[i] * fontSize > canvas.height && Math.random() > .95) {
          drops[i] = 0;
        }
      }
    }

    const intervalId = setInterval(draw, 33);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="login">
      <canvas ref={canvasRef} style={{ position: 'absolute', top: 0, left: 0, zIndex: -1 }} />
      <div className="card">
        <div className="left">
          <h1>Hello world!</h1>
          <p>welcome to my socialmedia site! this is a social media platform for developers, programmers, and everything alike.</p>
          <span>dont have an account?</span>
          <button className="register">register</button>
          <button className="guest">guest account</button>
        </div>
        <div className="right">
          <h1>login</h1>
          <form>
            <input type="text" placeholder="username" />
            <input type="password" placeholder="password" />
            <button>login</button>
          </form>        
        </div>
      </div>
    </div>
  );
}

export default Login;
