import "./update.scss"
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { makeRequest } from "../../axios";
import {useState} from 'react'
import { useContext } from 'react';
import { AuthContext } from '../../context/authContext';
export const Update = ({setOpenUpdate, user}) => {
  const { updateProfile } = useContext(AuthContext);
  const [cover,setCover] = useState(null)
  const [profile,setProfile] = useState(null)
  const [inputs, setInputs] = useState({
    name: "",
    website: "",
    city: "",
    twitter: "",
    linkedin: "",
    instagram: "",
    facebook: ""
});

  const handleChange= e => {
    setInputs((prev)=> ({...prev, [e.target.name]: e.target.value}));
  };

  const upload = async (file ) => {
    try {
      const formData = new FormData();
      formData.append("file", file);
      const res = await makeRequest.post("/upload", formData);
      return res.data;
    } catch (err) {
      console.log(err);
    }
  };


  const queryClient = useQueryClient();

  const mutation = useMutation(
    (user) => {
      return makeRequest.put("/users", user);
    },
    {
      onSuccess: (data) => {
        // Invalidate and refetch
        queryClient.invalidateQueries(["user"]);
        updateProfile(data);
      },
    }
  );

  const handleClick = async (e) => {
    e.preventDefault();

    let coverUrl;
    let profileUrl;
    coverUrl = cover ? await upload(cover) : user.coverpic;
    profileUrl = profile ? await upload(profile) : user.profilepic;

    const updatedInfo = {
      coverpic: coverUrl,
      profilepic: profileUrl
    };

    // Only include fields in the mutation that have values
    for (let key in inputs) {
      if (inputs[key].trim()) { // Check if the string is not empty or just whitespace
        updatedInfo[key] = inputs[key];
      }
    }

    mutation.mutate(updatedInfo);
    setOpenUpdate(false);
    setCover(null);
    setProfile(null);
  }

  return (
    <div className="update">
      <div className="update">
        <span>Update</span>

        <form action="">
          <span>Cover picture</span>
  <input type="file" onChange={e=>setCover(e.target.files[0])}/>
  <span>Profile picture</span>
  <input type="file" onChange={e=>setProfile(e.target.files[0])}/>
  <span>Name</span>
  <input type="text" name="name" onChange={handleChange}/>
  <span>Website</span>
  <input type="text" name="website" onChange={handleChange}/>
  <span>City</span>
  <input type="text" name="city" onChange={handleChange}/>

  <span>Twitter</span>
  <input type="text" name="twitter" onChange={handleChange} />

  <span>LinkedIn</span>
  <input type="text" name="linkedin" onChange={handleChange} />

  <span>Instagram</span>
  <input type="text" name="instagram" onChange={handleChange} />

  <span>Facebook</span>
  <input type="text" name="facebook" onChange={handleChange} />
  <button onClick={handleClick}>submit</button>
        </form>

  <button onClick={()=>setOpenUpdate(false)}>x</button>
      </div>
    </div>

  )
}
export default Update
