import HomeOutlinedIcon from '@mui/icons-material/HomeOutlined';
import DarkModeOutlinedIcon from '@mui/icons-material/DarkModeOutlined';
import GridViewIcon from '@mui/icons-material/GridView';
import WbSunnyOutlinedIcon from '@mui/icons-material/WbSunnyOutlined';
import NotificationsOutlinedIcon from '@mui/icons-material/NotificationsOutlined';
import EmailOutlinedIcon from '@mui/icons-material/EmailOutlined';
import PersonOutlinedIcon from '@mui/icons-material/PersonOutlined';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import {Link} from "react-router-dom"
import { useState, useEffect } from 'react';

import './navBar.scss'


import { useContext } from 'react';
import {DarkModeContext} from  "../../context/darkModeContext"
import {AuthContext} from '../../context/authContext'



const NavBar = () => {
  const [searchQuery, setSearchQuery] = useState(''); // To handle the input value of the search bar
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async () => {
    if (searchQuery.trim() !== '') {
      try {
        const response = await fetch(`/api/users/search?q=${searchQuery.trim()}`);
        const data = await response.json();
        setSearchResults(data);
      } catch (error) {
        console.error("Failed to fetch search results:", error);
      }
    }
  };


  const {currentUser} = useContext(AuthContext)

  const {toggle, darkMode} =useContext(DarkModeContext)

  return (
    <div className='navbar'>
<div className="left">
<Link to="/" className="homelink" style={{textDecoration:"none"}}>
    DevConnect
</Link>
<Link to="/" className="homelink" style={{textDecoration:"none"}}><HomeOutlinedIcon className='icon homelink'/></Link>


{darkMode ? <DarkModeOutlinedIcon className='icon' onClick={toggle}/> : <WbSunnyOutlinedIcon className='icon' onClick={toggle}/>}
<Link to="/" className="homelink" style={{textDecoration:"none"}}><PersonOutlinedIcon className='icon homelink'/></Link>
<div className='search'>
  <SearchOutlinedIcon />
  <input 
        type="text" 
        placeholder='Search' 
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        onKeyPress={(e) => e.key === 'Enter' && handleSearch()} // Search when pressing the 'Enter' key
      />
</div>
</div>

<div className="right">

<EmailOutlinedIcon className='icon'/>
<NotificationsOutlinedIcon className='icon'/>

<Link to={`/profile/${currentUser.id}`} style={{textDecoration: 'none'}}>

<div className='user'>
  <img src={"/upload/" + currentUser.profilepic} alt="picture" className='userpic' />
  <span>{currentUser.name}</span>
  </div>
  </Link>

</div>

    </div>
  )
}

export default NavBar
